<script>

export default {
    name: 'launch',

    data () {
        return {
            clientWidth: document.body.offsetWidth || document.documentElement.clientWidth || window.innerWidth, // 设备的宽度

            /**
             * 页面状态
             * @param {string} leisure 空闲
             * @param {string} notfree 非空闲/充电中
             * @param {string} offline 离线
             * @param {string} booting 启动中
             * @param {string} bootfailed 启动失败
             */
            pageState: 'leisure',

            StationName: '深圳信挚工业一期充电站', // 充电站名称
            
            GunName: '1号枪', // 充电枪名
        }
    },

    computed: {
        /**
         * 页面状态转换为中文
         */
        pageStateName: function () {
            let keyValueName = {
                leisure: '空闲',
                notfree: '充电中',
                offline: '离线',
                booting: '启动中',
                bootfailed: '启动失败',
            }

            return keyValueName[this.pageState];
        },

        /**
         * 页面状态转换为颜色
         */
        pageStateColor: function () {
            let keyValueColor = {
                leisure: '#00B90A',
                notfree: '#FF8D18',
                offline: '#999999',
                booting: '#5594FF',
                bootfailed: '#FF8D18',
            }

            return keyValueColor[this.pageState];
        },
    },

    mounted: function () {
        // 初始化页面状态
        this.pageState = this.$route.params.pageState;
    },

    methods: {},
}

</script>

